INSERT INTO `field_revision_body` VALUES ('node','page',0,1,1,'und',0,'<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" class=\"text01table\" width=\"100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"4\"><b>Experience by Design helps you:</b></td>\r\n		</tr>\r\n		<tr>\r\n			<td class=\"text01\" rowspan=\"2\"><img height=\"8\" src=\"http://www.experiencebydesign.com/images/spacer.gif\" width=\"15\" /></td>\r\n			<td align=\"left\" colspan=\"2\" valign=\"top\">\r\n			<table border=\"0\" cellpadding=\"0\" cellspacing=\"5\" width=\"100%\">\r\n				<tbody>\r\n					<tr>\r\n						<td align=\"left\" valign=\"top\">»</td>\r\n						<td>Work more efficiently and reduce time to market</td>\r\n					</tr>\r\n					<tr>\r\n						<td align=\"left\" valign=\"top\">»</td>\r\n						<td>Lower development costs by defining clear requirements that are accessible to all stakeholders, rightly aligned and focus attention on the salient issues</td>\r\n					</tr>\r\n					<tr>\r\n						<td align=\"left\" valign=\"top\">»</td>\r\n						<td>Communicate more effectively with your audience to increase sales, adoption and retention while reducing costs</td>\r\n					</tr>\r\n					<tr>\r\n						<td align=\"left\" valign=\"top\">»</td>\r\n						<td>Improve ease of use and relevancy, and increase customer service and satisfaction, while strengthening brand perception and loyalty</td>\r\n					</tr>\r\n				</tbody>\r\n			</table>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p class=\"text01\">Our focus on user experience provides the clarity and insight that allow us to shape your project in unique ways. <a href=\"/metodology\">Our methodology</a> takes you from discovery, to design and into development in a structured and focused way to ensure your project launches on time, on strategy and on budget. <a class=\"boxbodyC\" href=\"/services\">We tailor our services</a> to meet the needs of each engagement without unnecessary overhead.</p>\r\n\r\n<p class=\"text01\">See <a href=\"/projects\">some of the organizations that have benefited from our services</a> over the years.</p>\r\n','','filtered_html'),('node','page',0,2,2,'und',0,'<p><span class=\"textheader01\">Experience by Design customizes its services to the needs and realities of your business. We can collaborate with your internal project team, or work as an external partner to help you deliver a user-friendly web site or software product to market.</span></p>\r\n\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" class=\"text01table\" width=\"100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"4\"><b>User Experience Design Services</b></td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\"><img height=\"8\" src=\"http://www.experiencebydesign.com/images/spacer.gif\" width=\"15\" /></td>\r\n			<td align=\"left\" colspan=\"2\" valign=\"top\">\r\n			<table border=\"0\" cellpadding=\"0\" cellspacing=\"8\" width=\"100%\">\r\n				<tbody>\r\n					<tr>\r\n						<td align=\"left\" valign=\"top\">»</td>\r\n						<td>Product Ideation &amp; Prototyping</td>\r\n					</tr>\r\n					<tr>\r\n						<td align=\"left\" valign=\"top\">»</td>\r\n						<td>User Experience Modeling</td>\r\n					</tr>\r\n					<tr>\r\n						<td align=\"left\" valign=\"top\">»</td>\r\n						<td>Task Analysis &amp; Interaction Design</td>\r\n					</tr>\r\n					<tr>\r\n						<td align=\"left\" valign=\"top\">»</td>\r\n						<td>Information Architecture &amp; Navigation Design</td>\r\n					</tr>\r\n					<tr>\r\n						<td align=\"left\" valign=\"top\">»</td>\r\n						<td>Content Strategy</td>\r\n					</tr>\r\n					<tr>\r\n						<td align=\"left\" valign=\"top\">»</td>\r\n						<td>User Interface Design</td>\r\n					</tr>\r\n					<tr>\r\n						<td align=\"left\" valign=\"top\">»</td>\r\n						<td>Information &amp; Graphic Design</td>\r\n					</tr>\r\n				</tbody>\r\n			</table>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" class=\"text01table\" width=\"100%\">\r\n	<tbody>\r\n